mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.yonghu_delete as yonghuDelete ,zhaopin.gongsi_id as zhaopinGongsiId ,zhaopin.zhaopin_name as zhaopinName ,zhaopin.zhaopin_photo as zhaopinPhoto ,zhaopin.zhaopin_address as zhaopinAddress ,zhaopin.lianxiren_name as lianxirenName ,zhaopin.zhaopin_phone as zhaopinPhone ,zhaopin.zhaopin_types as zhaopinTypes ,zhaopin.zhaopin_renshu_number as zhaopinRenshuNumber ,zhaopin.zhaopin_content as zhaopinContent
FROM toudi a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id left JOIN zhaopin zhaopin ON a.zhaopin_id = zhaopin.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and ( a.zhaopin_id = #{params.zhaopinId} )
and a.toudi_yesno_types = #{params.toudiYesnoTypes}
and a.toudi_yesno_text like CONCAT('%',#{params.toudiYesnoText},'%')
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
and a.zhaopin_id IS NOT NULL
and zhaopin.gongsi_id = #{params.gongsiId}
and zhaopin.zhaopin_name like CONCAT('%',#{params.zhaopinName},'%')
and zhaopin.zhaopin_address like CONCAT('%',#{params.zhaopinAddress},'%')
and zhaopin.lianxiren_name like CONCAT('%',#{params.lianxirenName},'%')
and zhaopin.zhaopin_phone like CONCAT('%',#{params.zhaopinPhone},'%')
and zhaopin.zhaopin_types = #{params.zhaopinTypes}
and zhaopin.zhaopin_renshu_number >= #{params.zhaopinRenshuNumberStart}
and zhaopin.zhaopin_renshu_number <= #{params.zhaopinRenshuNumberEnd}
and zhaopin.zhaopin_renshu_number = #{params.zhaopinRenshuNumber}
and zhaopin.zhaopin_content like CONCAT('%',#{params.zhaopinContent},'%')
order by a.${params.orderBy} desc
